#include <bits/stdc++.h>
using namespace std;
#define INTERVAL 100000

int main(){
    long double rand_x, rand_y, r = 1.0, pi;
    long long num_of_circles = 0, num_of_squares = 0;
    for(long long i = 0; i < (long)INTERVAL*INTERVAL; i++){
        rand_x = (long double)(rand() % (INTERVAL + 1))/INTERVAL;
        rand_y = (long double)(rand() % (INTERVAL + 1))/INTERVAL;

        if(pow(rand_x, 2) + pow(rand_y, 2) <= r) num_of_circles++;
        
        num_of_squares++;

        if(i%10000000 == 0){
            pi = (long double)(4*num_of_circles)/num_of_squares;
            cout << "Iteration number: " << i << " and value of pi is: " << setprecision(60) << pi << endl;
        }
    }
    pi = (long double)(4*num_of_circles)/num_of_squares;

    cout << endl << "Final value of pi: " << setprecision(60) << pi << endl;
}


